-- إضافة التخصصات الجديدة مع معلوماتها التفصيلية

-- أولاً، إضافة أعمدة جديدة لجدول majors لتخزين المعلومات الإضافية
ALTER TABLE public.majors 
ADD COLUMN IF NOT EXISTS description TEXT,
ADD COLUMN IF NOT EXISTS category TEXT,
ADD COLUMN IF NOT EXISTS duration TEXT,
ADD COLUMN IF NOT EXISTS career_opportunities TEXT[];

-- إدراج التخصصات الطبية
INSERT INTO public.majors (name, name_en, description, category, duration, career_opportunities, faculty_id) VALUES
('الطب البشري', 'Human Medicine', 'تخصص يهدف إلى إعداد أطباء مؤهلين لتشخيص وعلاج الأمراض البشرية، ويشمل دراسة علوم التشريح والفيزيولوجيا والمرضية والدواء والجراحة', 'طبي', '6 سنوات', ARRAY['طبيب عام', 'طبيب مختص', 'طبيب في المستشفيات', 'طبيب عيادات خاصة', 'باحث طبي'], (SELECT id FROM public.faculties WHERE name LIKE '%طب%' LIMIT 1)),

('طب الأسنان', 'Dentistry', 'تخصص يركز على دراسة وعلاج أمراض الفم والأسنان واللثة، ويشمل الوقاية والعلاج والجراحة وتقويم الأسنان وطب أسنان الأطفال', 'طبي', '5 سنوات', ARRAY['طبيب أسنان عام', 'اختصاصي تقويم أسنان', 'جراح فم ووجه وفكين', 'طبيب أسنان أطفال', 'اختصاصي أمراض اللثة'], (SELECT id FROM public.faculties WHERE name LIKE '%طب%' LIMIT 1)),

('الصيدلة', 'Pharmacy', 'تخصص يهتم بدراسة الأدوية وتحضيرها وتصنيعها وتأثيراتها، بالإضافة إلى الكيمياء الدوائية والسموم والرقابة الدوائية', 'طبي', '5 سنوات', ARRAY['صيدلي مجتمعي', 'صيدلي مستشفيات', 'صيدلي صناعي', 'مراقب دوائي', 'باحث في التطوير الدوائي'], (SELECT id FROM public.faculties WHERE name LIKE '%صيدل%' LIMIT 1)),

('التمريض', 'Nursing', 'تخصص يركز على رعاية المرضى وتقديم الخدمات الصحية الأولية والثانوية، ويشمل التمريض العام والتخصصي والإدارة الصحية', 'طبي', '4 سنوات', ARRAY['ممرض/ة عام', 'ممرض/ة مختص', 'مدير تمريض', 'ممرض/ة عناية مركزة', 'ممرض/ة صحة مجتمعية'], (SELECT id FROM public.faculties WHERE name LIKE '%تمريض%' OR name LIKE '%طب%' LIMIT 1));

-- إدراج التخصصات الهندسية
INSERT INTO public.majors (name, name_en, description, category, duration, career_opportunities, faculty_id) VALUES
('الهندسة المدنية', 'Civil Engineering', 'تخصص يشمل تصميم وتنفيذ المشاريع الإنشائية مثل الجسور والمباني والطرق والسدود وأنظمة المياه والصرف الصحي', 'هندسي', '5 سنوات', ARRAY['مهندس إنشائي', 'مهندس طرق', 'مهندس موارد مائية', 'مدير مشاريع', 'مهندس استشاري'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة المعمارية', 'Architecture', 'تخصص يجمع بين الفن والعلم في تصميم المباني والمساحات، ويشمل التصميم المعماري والتخطيط العمراني والتصميم الداخلي', 'هندسي', '5 سنوات', ARRAY['مهندس معماري', 'مصمم داخلي', 'مخطط عمراني', 'مستشار معماري', 'مدير مشاريع معمارية'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة البحرية', 'Marine Engineering', 'تخصص يركز على تصميم وبناء وصيانة السفن والمنشآت البحرية، ويشمل الهندسة البحرية والنقل البحري والموانئ', 'هندسي', '5 سنوات', ARRAY['مهندس بحري', 'مهندس سفن', 'مهندس موانئ', 'مفتش بحري', 'مصمم منشآت بحرية'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة الجيوماتية والمساحية', 'Surveying Engineering', 'تخصص يهتم بقياس ورسم خرائط الأراضي والمناطق، ويشمل المساحة الأرضية والجوية ونظم المعلومات الجغرافية', 'هندسي', '5 سنوات', ARRAY['مهندس مساحة', 'مهندس خرائط', 'مختص GIS', 'مهندس مساحة جوية', 'مستشار جيوماتي'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة المعلوماتية', 'Informatics Engineering', 'تخصص يجمع بين علوم الحاسوب والهندسة، ويشمل تطوير البرمجيات وهندسة الشبكات وأنظمة المعلومات', 'هندسي', '5 سنوات', ARRAY['مهندس برمجيات', 'مهندس شبكات', 'مطور تطبيقات', 'مهندس أنظمة', 'مختص أمن معلومات'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة الكهربائية', 'Electrical Engineering', 'تخصص يدرس الكهرباء والإلكترونيات والطاقة، ويشمل هندسة القوى والتحكم والاتصالات والطاقات المتجددة', 'هندسي', '5 سنوات', ARRAY['مهندس كهرباء', 'مهندس طاقة', 'مهندس تحكم', 'مهندس اتصالات', 'مهندس طاقة متجددة'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة الميكانيكية', 'Mechanical Engineering', 'تخصص يدرس الآلات والمحركات والأنظمة الميكانيكية، ويشمل التصميم والتصنيع والطاقة والتبريد والتكييف', 'هندسي', '5 سنوات', ARRAY['مهندس ميكانيكي', 'مهندس تصنيع', 'مهندس طاقة', 'مهندس تبريد وتكييف', 'مصمم آلات'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة الزراعية', 'Agricultural Engineering', 'تخصص يطبق المبادئ الهندسية في الزراعة، ويشمل آلات زراعية وري وصرف وهندسة الأراضي والموارد المائية', 'هندسي', '5 سنوات', ARRAY['مهندس زراعي', 'مهندس ري وصرف', 'مهندس آلات زراعية', 'مهندس موارد مائية زراعية', 'مستشار زراعي'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' OR name LIKE '%زراع%' LIMIT 1)),

('هندسة الميكاترونكس', 'Mechatronics Engineering', 'تخصص متعدد التخصصات يجمع بين الهندسة الميكانيكية والكهربائية وعلوم الحاسوب، ويركز على الأنظمة الذكية والروبوتات', 'هندسي', '5 سنوات', ARRAY['مهندس ميكاترونكس', 'مهندس روبوتات', 'مهندس أتمتة', 'مهندس أنظمة ذكية', 'مطور أنظمة تحكم'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1)),

('الهندسة النفطية', 'Petroleum Engineering', 'تخصص يركز على استخراج وإنتاج النفط والغاز الطبيعي، ويشمل حفر الآبار وهندسة المكامن والتكرير', 'هندسي', '5 سنوات', ARRAY['مهندس نفط', 'مهندس حفر', 'مهندس مكامن', 'مهندس إنتاج', 'مهندس تكرير'], (SELECT id FROM public.faculties WHERE name LIKE '%هندس%' LIMIT 1));

-- إدراج التخصصات العلمية والإنسانية
INSERT INTO public.majors (name, name_en, description, category, duration, career_opportunities, faculty_id) VALUES
('العلوم', 'Sciences', 'تخصص يدرس العلوم الطبيعية مثل الفيزياء والكيمياء والرياضيات وعلوم الأرض، ويهدف إلى فهم الظواهر الطبيعية', 'علمي', '4 سنوات', ARRAY['باحث علمي', 'مدرس علوم', 'محلل مختبرات', 'مختص جودة', 'مستشار علمي'], (SELECT id FROM public.faculties WHERE name LIKE '%علوم%' LIMIT 1)),

('العلوم السياسية', 'Political Science', 'تخصص يدرس النظم السياسية والعلاقات الدولية والسياسات العامة، ويهتم بفهم السلوك السياسي والحكم', 'علوم اجتماعية', '4 سنوات', ARRAY['دبلوماسي', 'محلل سياسي', 'مستشار سياسي', 'موظف دولي', 'باحث سياسي'], (SELECT id FROM public.faculties WHERE name LIKE '%علوم%' OR name LIKE '%سياس%' LIMIT 1)),

('الآداب والعلوم الإنسانية', 'Arts and Humanities', 'تخصص يدرس اللغات والأدب والتاريخ والفلسفة والثقافة، ويهدف إلى فهم التراث الإنساني والثقافي', 'علوم إنسانية', '4 سنوات', ARRAY['مدرس', 'كاتب ومؤلف', 'مترجم', 'باحث تراث', 'محرر ثقافي'], (SELECT id FROM public.faculties WHERE name LIKE '%آداب%' OR name LIKE '%إنسان%' LIMIT 1)),

('الحقوق', 'Law', 'تخصص يدرس القوانين والتشريعات والنظم القانونية، ويشمل القانون المدني والجنائي والإداري والدولي', 'قانوني', '4 سنوات', ARRAY['محامي', 'قاضي', 'مستشار قانوني', 'موثق', 'مدعي عام'], (SELECT id FROM public.faculties WHERE name LIKE '%حقوق%' OR name LIKE '%قانون%' LIMIT 1)),

('التربية', 'Education', 'تخصص يركز على إعداد المعلمين وتطوير المناهج وطرق التدريس، ويشمل علم النفس التربوي وإدارة التعليم', 'تربوي', '4 سنوات', ARRAY['معلم', 'مدير مدرسة', 'مشرف تربوي', 'مطور مناهج', 'باحث تربوي'], (SELECT id FROM public.faculties WHERE name LIKE '%تربي%' LIMIT 1)),

('الإعلام', 'Media and Communication', 'تخصص يدرس وسائل الإعلام والاتصال، ويشمل الصحافة والإذاعة والتلفزيون والإعلام الرقمي والعلاقات العامة', 'إعلامي', '4 سنوات', ARRAY['صحفي', 'مذيع', 'محرر إعلامي', 'مختص علاقات عامة', 'منتج إعلامي'], (SELECT id FROM public.faculties WHERE name LIKE '%إعلام%' OR name LIKE '%اتصال%' LIMIT 1)),

('الشريعة', 'Islamic Studies', 'تخصص يدرس العلوم الشرعية والفقه الإسلامي والتفسير والحديث، ويهدف إلى فهم وتطبيق الأحكام الشرعية', 'شرعي', '4 سنوات', ARRAY['إمام وخطيب', 'قاضي شرعي', 'مدرس علوم شرعية', 'مفتي', 'باحث شرعي'], (SELECT id FROM public.faculties WHERE name LIKE '%شريع%' OR name LIKE '%إسلام%' LIMIT 1));